def density(sg,temperature):
                api = (141.5/sg) - 131.5
                if api <=14.9:
                    eta= 0.00035
                elif api <= 34.9:
                    eta= 0.0004
                elif api <= 50.9:
                    eta= 0.0005
                elif api <= 63.9:
                    eta= 0.0006
                elif api <= 78.9:
                    eta= 0.0007
                elif api <= 88.9:
                    eta= 0.0008
                elif api <= 93.9:
                    eta= 0.00035 
                elif api <= 100:
                    eta= 0.0009   
                else: st.write("please enter a valid Specific gravity or use user-defined compositions in Gases' option") 
                density = sg*1000*(1-eta*((temperature*1.8+32)-60))
                return density

def thermo_prop(sg,t,prop_calc_table, two_points , fluid_allocation,rw):
        t = 1.8*t+32
        thermal_coductivity = (0.813/sg)*(1-0.0003*(t-32)) *0.14422790000000002
        cp = (1/np.sqrt(sg))*(0.388+0.00045*t)
        cv = cp-(0.09/sg)
        latent_heat = (1/sg)*(110.9-0.09*t) * 0.555927
        #if two_points != 'Yes'  :
        prop_calc_table.loc['thermal conductivity',fluid_allocation]= thermal_coductivity
            
        #if two_points != 'Yes' :
        prop_calc_table.loc['Cp',fluid_allocation]= cp
            
        prop_calc_table.loc['Cv',fluid_allocation] = cv
        prop_calc_table.loc['latent heat',fluid_allocation]= latent_heat
        
        return prop_calc_table
def vis_1point(t,analysis_temp,analysis_mu,sg,unit):
    if not unit:
        T =t
        mu = analysis_mu
        c = -0.8696
        b= np.log10(mu)-c
        s = 0.2008*b+1.6180
        log_mu = (b/(1+((T-analysis_temp)/310.93))**s)+c
        mu_calc =10**log_mu
        mu_calc = mu_calc/(0.001*density(sg,T))
    else:
        T =t
        mu = analysis_mu/(0.001*density(sg,analysis_temp))
        c = -0.8696
        b= np.log10(mu)-c
        s = 0.2008*b+1.6180
        log_mu = (b/(1+((T-analysis_temp)/310.93))**s)+c
        mu_calc =(10**log_mu)*(0.001*density(sg,analysis_temp))
    return mu_calc
